package pms.a01_Yeji.a04_VO;

import java.util.Date;


public class MeetingBoardFile {
	private int recordNum;
	private String mrFilePath;
	private String mrFileName;
	private Date recordRegdte;
	private Date recordUptdte;
	private String etc;
	public MeetingBoardFile() {
		// TODO Auto-generated constructor stub
	}
	
	public MeetingBoardFile(String mrFilePath, String mrFileName) {
		this.mrFilePath = mrFilePath;
		this.mrFileName = mrFileName;
	}

	public MeetingBoardFile(int recordNum, String mrFilePath, String mrFileName, Date recordRegdte, Date recordUptdte,
			String etc) {
		this.recordNum = recordNum;
		this.mrFilePath = mrFilePath;
		this.mrFileName = mrFileName;
		this.recordRegdte = recordRegdte;
		this.recordUptdte = recordUptdte;
		this.etc = etc;
	}
	public int getRecordNum() {
		return recordNum;
	}
	public void setRecordNum(int recordNum) {
		this.recordNum = recordNum;
	}
	public String getMrFilePath() {
		return mrFilePath;
	}
	public void setMrFilePath(String mrFilePath) {
		this.mrFilePath = mrFilePath;
	}
	public String getMrFileName() {
		return mrFileName;
	}
	public void setMrFileName(String mrFileName) {
		this.mrFileName = mrFileName;
	}
	public Date getRecordRegdte() {
		return recordRegdte;
	}
	public void setRecordRegdte(Date recordRegdte) {
		this.recordRegdte = recordRegdte;
	}
	public Date getRecordUptdte() {
		return recordUptdte;
	}
	public void setRecordUptdte(Date recordUptdte) {
		this.recordUptdte = recordUptdte;
	}
	public String getEtc() {
		return etc;
	}
	public void setEtc(String etc) {
		this.etc = etc;
	}
	
}
